$pidlist = array(
'jd_20070405',
'ms_imageshack_upload',
'Photobucket_Uploader',
);
echo "<br />";
echo "<center>Installing or upgrading <strong>Imageshack, MEGAUPLOAD & Photobucket Uploader</strong>.</center>";
echo "<br />";
echo "<center>Please remember to <strong>Mark as Installed</strong> at vBulletin.org to recieve support.</center>";
echo "<br />";
echo "<center>Install Information</center>";
echo "<br />";
vbflush(); 
if (!empty($pidlist))
{
	foreach ($pidlist as $oldpid)
	{
		$check = $db->query_first_slave("
			SELECT version FROM ".TABLE_PREFIX."product WHERE productid = '".$db->escape_string($oldpid)."'
		");
		if ($check['version'])
		{
			delete_product($oldpid);
			echo "<center><font color='red'>A previous version of this hack has been detected and automatically been removed : pid = $oldpid</font></center>";
			echo "<br />";
			vbflush(); 
			sleep(2);
		}
	}
}
