if ($vbulletin->userinfo['permissions']['ms_imageshack_upload_permissions'] & $vbulletin->bf_ugp['ms_imageshack_upload_permissions']['imageshack_editpost'] || $vbulletin->userinfo['permissions']['ms_megaupload_upload_permissions'] & $vbulletin->bf_ugp['ms_megaupload_upload_permissions']['megaupload_editpost'] || $vbulletin->userinfo['permissions']['ms_photobucket_upload_permissions'] & $vbulletin->bf_ugp['ms_photobucket_upload_permissions']['photobucket_editpost'])
{
    $show['imageshack_is'] = false;
    $show['imageshack_mu'] = false;
    $show['imageshack_pb'] = false;
    $imageshack_ep = 0;
    if ($vbulletin->options['megaupload_size'] == 0)
    {
        $megaupload_width = 232;
        $megaupload_height = 143;
    }
    elseif ($vbulletin->options['megaupload_size'] == 1)
    {
        $megaupload_width = 350;
        $megaupload_height = 143;
    }
    elseif ($vbulletin->options['megaupload_size'] == 2)
    {
        $megaupload_width = 350;
        $megaupload_height = 90;
    }
    if($vbulletin->userinfo['permissions']['ms_imageshack_upload_permissions'] & $vbulletin->bf_ugp['ms_imageshack_upload_permissions']['imageshack_editpost'])
    {
        $imageshack_ep = 1;
        $show['imageshack_is'] = true;
    }
    if($vbulletin->userinfo['permissions']['ms_megaupload_upload_permissions'] & $vbulletin->bf_ugp['ms_megaupload_upload_permissions']['megaupload_editpost'])
    {
        $imageshack_ep = 1;
        $show['imageshack_mu'] = true;
    }
    if($vbulletin->userinfo['permissions']['ms_photobucket_upload_permissions'] & $vbulletin->bf_ugp['ms_photobucket_upload_permissions']['photobucket_editpost'])
    {
        $imageshack_ep = 1;
        $show['imageshack_pb'] = true;
    }
    if ($imageshack_ep == 1)
	{
            $imageshack_ep_search_text = '$attachmentoption'; 
	    $vbulletin->templatecache['editpost'] = str_replace($imageshack_ep_search_text,$imageshack_ep_search_text.fetch_template('imageshack'),$vbulletin->templatecache['editpost']);
	}
}

